import React, { useState } from "react";
import { Table } from "reactstrap";
import Row from "../../../../component/Row/Row";
import SearchWord from "../../../../component/SearchWord/SearchWord";
import { useOwnDictionary } from "../../../../hooks/useOwnDictionary";
import DataWords from "./DataWords";
import "./OwnDictionary.scss";

const OwnDictionary: React.FC = () => {
  const ownDictionary = useOwnDictionary();
  const [active, setActive] = useState("");

  const newActive = (word: string) => {
    if(active === word) {
      setActive("");
    }else {
      setActive(word);
    };
  };

  const listWords = ownDictionary.map((data) => <DataWords data={data} active={active} newActive={newActive}/>);
  const dictionaryTabs = ownDictionary.map((_, idx) => <button className="btn btn-outline-info">{idx}</button>)

  console.log(ownDictionary, "ownDictionary")
  return (
    <div className="own-dictionary">
      <Row
        Left={<div>
          <div className="dictionary-tabs">
            {dictionaryTabs}
          </div>
          <Table className="bg-light" style={{height: "100%"}}>{!ownDictionary && listWords}</Table>
        </div>}
        Right={<SearchWord />}
        fixed="right"
      />
    </div>
  )
};

export default OwnDictionary;
