  return (
    <div className="own-dictionary">
      <Row
        Left={<div>
          <div className="dictionary-tabs">
            {dictionaryTabs}
          </div>
          <Table className="bg-light" style={{height: "100%"}}>{listWords}</Table>
        </div>}
        Right={<SearchWord />}
        fixed="right"
      />
    </div>
  )
};

export default OwnDictionary;
