import React from 'react';
import {Field} from 'formik';
import { ViewProps } from './types';
import './Tests.scss';

const View: React.FC<ViewProps> = ({
  test, 
  count = 0, 
  start, 
  children, 
  toggleOptions, 
  options, 
  randomWords,
}) => {

	return (
		<div className="test-page-inner">
				<button type="button" className="btn btn-outline-success" onClick={() => toggleOptions()} disabled={start}>OPTIONS</button>     
				<div className="">
					{start && 
						<div>
							<h1 className="title">{test.name}</h1>
							<p className="text__under-title">en - ua</p>
							<h2 className="title">
								{
									start? 
									test.words[randomWords[count]].en : 
									test.words[randomWords[count]].ua 
								}
							</h2>
							<Field className=""
								name="answer" 
								type="text" 
								placeholder="GO WRITE" 
								id="inputLarge" 
							/>
						</div>
					}
          <div>{ children }</div>
		  </div>
	  </div>
	)
};

export default View;
