import axios from "axios";
import { Field, Form, Formik } from "formik";
import React, { useEffect, useState } from "react"
import "./SearchWord.scss";

const getResource = async (url: string) => {
  axios.get(url)
   .then((d) => d)
   .catch(e => console.log(e, "un known word!"));
 };

const SearchWord: React.FC = () => {
  const API_BASE = "https://api.dictionaryapi.dev/api/v2/entries/en/";

  interface Data {
    word: string,
    phonetics: {
      audio: string
      text: string
    }[], 
    meanings: {partOfSpeech: string, definitions: {
      definition: string, 
      example: string, 
      synonyms: string[], 
      antonyms: string[],
    }[]}[],
    origin: string,
    phonetic: string
  };

  const [data, setData] = useState<Data[]>([]);
  const [loading, setLoading] = useState(false);

  interface InitialState {
    word: string
  }

  const initialState : InitialState= {
    word: "",
  }

  const onSubmit = async (values: InitialState) => {
    setLoading(true);
    axios.get(API_BASE + values.word)
    .then((d: any) => {
      setData(d.data);
      setLoading(false);
    })
    .then(() => console.log(data))
  };

  useEffect(() => console.log("update"), [data]);

  return (
    <div className="search-word col-12">
      <Formik  
        onSubmit={onSubmit}
        initialValues={initialState}
      >
        {
          () => {
            return (
              <Form className="search-panel col-12">
                <Field className="form-control btn-bg" placeholder="word" name="word" />
                <div className="info-panel" onClick={() => console.log(data)}>
                  {
                    data?.map((d: Data) => {
                      return (
                        <div>
                          <h4>{d.word}</h4>
                          <p>
                            phonetics: audio - {d.phonetics[0].audio} text - {d.phonetics[0].text}
                          </p>
                          <p>

                          </p>
                        </div>
                      )
                    })
                  }
                  { loading && "Loading..."}
                </div>
              </Form>
            )
          }
        }
      </Formik>
    </div>
  )
};

export default SearchWord;
