import { Formik } from 'formik';
import React from 'react';
import { Form, FormGroup, Label, Input, FormFeedback, FormText } from 'reactstrap';
import * as Yup from 'yup';
import { BTN_BG } from '../../classes';
// import SignForm from './component/Sign-form/Sign-form';
// import SignOut from './component/Sign-out/Sign-out';
import "./Sign-in.scss";

const SignIn: React.FC = () => {

  interface I {
    name: string,
    password: string,
    repeatPassword: string,
    email: string,
    submit: boolean,
  }

  const initialValues: I = {
    name: "",
    password: "",
    repeatPassword: "",
    email: "",
    submit: false,
  }

  const onSubmit = (values: I) => {
    console.log(values);
  };

  const schema = Yup.object().shape({
    name: Yup.string()
    .min(2, 'Too Short!')
    .max(50, 'Too Long!')
    .required('Required'),
    password: Yup.string().required('Required'),
    repeatPassword: Yup.string().required('Required'),
    email: Yup.string().email('Invalid email').required('Required'),
  });

  return (
    <div className="page">
      <div className="sign-in">
        <div className="sign-in__inner col-12 col-lg-4">
          <Formik 
            initialValues={initialValues}
            schema={schema}
            onSubmit={() => {

            }}
          >
           {
             ({errors}) => {
               return (
                <Form>
                  <FormGroup>
                    <Label for="exampleEmail">Input without validation</Label>
                    <Input name=""/>
                    <FormFeedback>You will not be able to see this</FormFeedback>
                    <FormText>Example help text that remains unchanged.</FormText>
                  </FormGroup>
                  <FormGroup>
                    <Label for="exampleEmail">Valid input</Label>
                    <Input valid />
                    <FormFeedback valid>Sweet! that name is available</FormFeedback>
                    <FormText>Example help text that remains unchanged.</FormText>
                  </FormGroup>
                  <FormGroup>
                    <Label for="examplePassword">Invalid input</Label>
                    <Input invalid />
                    <FormFeedback>Oh noes! that name is already taken</FormFeedback>
                    <FormText>Example help text that remains unchanged.</FormText>
                  </FormGroup>
                  <FormGroup>
                    <Label for="exampleEmail">Input without validation</Label>
                    <Input />
                    <FormFeedback tooltip>You will not be able to see this</FormFeedback>
                    <FormText>Example help text that remains unchanged.</FormText>
                  </FormGroup>
                  <FormGroup className="position-relative">
                    <Label for="exampleEmail">Valid input</Label>
                    <Input valid />
                    <FormFeedback valid tooltip>Sweet! that name is available</FormFeedback>
                    <FormText>Example help text that remains unchanged.</FormText>
                  </FormGroup>
                  <FormGroup className="position-relative">
                    <Label for="examplePassword">Invalid input</Label>
                    <Input invalid />
                    <FormFeedback tooltip>Oh noes! that name is already taken</FormFeedback>
                    <FormText>Example help text that remains unchanged.</FormText>
                  </FormGroup>
                  <FormGroup className="position-relative">
                  <button className={BTN_BG}>SUBMIT</button>
                  </FormGroup>
                </Form>
               )
             }
           }
          </Formik>
        </div>
      </div>
    </div>
  );
}

export default SignIn
