import React, { useRef } from 'react';
import "./Setting.scss";
import avatar from "../../../../images/photo.png";
import fileReader from '../../../../specialFunction/file-reader';
import { Formik } from 'formik';

const Setting: React.FC = () => {
  const ref = useRef(null);

  const onSubmit = (values: any) => {

  };

  return (
    <Formik initialValues={{}} onSubmit={(values: any) => onSubmit(values)}>
      {({values}) => {
        return (
          <div className="setting-page">
            <div className="user-data">
              <header>
                <div className="set-fields">
                  <input type="text" />
                  <input type="text" />
                </div>
                <div className="set-photo">
                  <img src={avatar} className="avatar" id="avatar" />
                  <input type="file" className="btn btn-outline-info" accept="image/*" ref={ref} onChange={(e) => fileReader(e.target, ref)}/>
                </div>
              </header>
            </div>
          </div>
        )
      }}
    </Formik>
  )
}

export default Setting;
