import React, { useRef } from 'react';
import "./Setting.scss";
import avatar from "../../../../images/photo.jpeg";
import fileReader from '../../../../specialFunction/file-reader';
import { Field, Formik } from 'formik';
import { FormGroup, Label } from 'reactstrap';

const Setting: React.FC = () => {
  const ref = useRef(null);

  const onSubmit = (values: any) => {

  };

  return (
    <Formik initialValues={{}} onSubmit={(values: any) => onSubmit(values)}>
      {({values}) => {
        return (
          <div className="setting-page">
            <div className="user-data">
              <header className="user-data-header">
                <div className="set-fields col-6">
                  <h2>Change</h2>
                  <FormGroup>
                    <Label for="Name">Change Name</Label>
                    <Field className="form-control" type="text" placeholder="name" name="name" id="name" />
                  </FormGroup>
                  <FormGroup>
                    <Label for="Password">Change Password</Label>
                    <Field className="form-control" type="text" placeholder="password" name="password" id="password" />
                  </FormGroup>
                  <FormGroup>
                    <Label for="Name">Change Email</Label>
                    <Field className="form-control" type="text" placeholder="email" name="email" id="email" />
                  </FormGroup>
                </div>
                <div className="set-photo">
                  <FormGroup>
                    <img src={avatar} className="avatar" id="avatar" />
                    <Label>Change Image</Label>
                    <input type="file" className="btn btn-outline-info" accept="image/*" ref={ref} onChange={(e: any) => fileReader(e.target, ref)}/>
                  </FormGroup>
                </div>
              </header>
              <div className="user-data-body">
                <h2>Change</h2>
              </div>

              <div className="user-data-footer">

              </div>
            </div>
          </div>
        )
      }}
    </Formik>
  )
}

export default Setting;
