import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Table } from "reactstrap";
import { BTN_BG } from "../../../../classes";
import Row from "../../../../component/Row/Row";
import SearchWord from "../../../../component/SearchWord/SearchWord";
import { useOwnDictionary } from "../../../../hooks/useOwnDictionary";
import { generateString } from "../../../../specialFunction/specialFunction";
import { add_word_own_dictionary } from "../../../../store/slices/authReducer";
import "./OwnDictionary.scss";

const OwnDictionary: React.FC = () => {
  const ownDictionary = useOwnDictionary();
  const dispatch = useDispatch();
  const [active, setActive] = useState();

  const listWords = ownDictionary.map((d) => {
    return (
     <>
      <tr>
        <th>{d.word}</th>
        <th className="option" onClick={() => setActive(d)}>...</th>
      </tr>
      <div key={generateString()} className={active === d.word? "show" : "close"}>
      <h4>{d.word}</h4>
      <p><b>Phonetics:</b> {d?.phonetics[0]?.text}</p>
      <p><b>Meanings:</b> part of speech: {d?.meanings[0]?.partOfSpeech}<br /></p>
      <p><b>Definitions:</b> {d?.meanings[0]?.definitions[0]?.definition}<br /></p>
      <p><b>Synonyms:</b> {d?.meanings[0]?.definitions[0]?.synonyms.map((synonym: string) => <span>{synonym}, </span>)}<br /></p>
      <p><b>Example:</b> {d?.meanings[0]?.definitions[0]?.example}<br /></p>
      <p><b>Origin</b> {d?.origin}<br /></p>
    </div>
      </>
    )
  });

  return (
    <div className="own-dictionary">
      <Row
        Left={<Table className="bg-light" style={{height: "100%"}}>{listWords}</Table>}
        Right={<SearchWord />}
        fixed="right"
      />
    </div>
  )
};

export default OwnDictionary;
