import {createSlice} from '@reduxjs/toolkit';
import { AuthReducer } from './types';

const initialState: AuthReducer = {
  authentication: true,
  role: "admin",
  name: "oleg",
  surname: "salabay",
  email: "olegsalabaymac@gmail.com",
  photo: "",
  describe: "",
  password: "1234",
  dictionary: [
    {
      part: "one", 
      words: [],
    }
  ],
};

const auth = createSlice({
    name: "auth",
    initialState,
    reducers: {
      log_out: (state) => {
        state.authentication = false
      },
      sign_in: (state) => {
        state.authentication = true
      },
      add_part_own_dictionary: (state, {payload}) => {
        state.dictionary = [...state.dictionary, payload];
      },
      add_word_own_dictionary: (state, {payload}) => {
        const foundedPart = state.dictionary.length
        state.dictionary[foundedPart - 1].words = [...state.dictionary[foundedPart - 1].words, ...payload.word];
      },
      remove_word: (state, {payload}) => {
        const removeWord = payload.word;
        const findPart = state.dictionary.findIndex((part) => part.part === payload.part);
        const itWordID = state.dictionary[findPart].words.findIndex((word) => word.word === removeWord);

        state.dictionary[findPart].words = [
          ...state.dictionary[findPart].words.slice(0, itWordID), 
          ...state.dictionary[findPart].words.slice(itWordID + 1, )
        ]
      },
    },
});

export const { log_out, sign_in, add_word_own_dictionary, add_part_own_dictionary, remove_word } = auth.actions;

export default auth.reducer;
