import React, {useState} from 'react';
import { useDispatch } from 'react-redux';
import { Redirect } from 'react-router';
import { generateString } from '../../specialFunction/specialFunction';
import { set_test } from '../../store/slices/testsReducer';
import CornModal from './Modal';
import "./Modal.scss";
import { ModalInfoTypes } from './types';

const ModalInfo: React.FC<ModalInfoTypes> = ({
  isModal, 
  toggle,
  test,
}) => {
  const [isPassTest, setIsPassTest] = useState(false);
  const dispatch = useDispatch();

  const passTest = () => {
    toggle();
    setIsPassTest(!isPassTest);
    dispatch(set_test({name: test.name, words: test.words}));
  };

  if(isPassTest) {
    return <Redirect to="/tests" />
  };

  return (
    <CornModal 
      isModal={isModal} 
      toggle={toggle} 
      isCancel 
      isConfirm 
      confirm={passTest}
    >
      <tbody className="words-list">
        {
          test?.words.map((word: {en: string, ua: string}) => {
            return (
              <tr key={generateString()}>
                <td>{word.en}</td>
                <td>{word.ua}</td>
              </tr>
            )
          })
        }
      </tbody>
   </CornModal>
  );
};

const ModalCreated: React.FC<any> = ({
  modal, 
  toggle,
}) => {
  const dispatch = useDispatch();

  interface InitialValues {
    name: string,
    options: {
      amount: number,
      type: string,
      timer: number,
    },
    words: { 
      en: string, 
      ua: string 
    }[],
  }

  const initialValues = {
    name: "",
    words: [],
    options: {
      amount: 10,
      type: "en-ua",
      timer: 60,
    },
  }

  const allWordsList = allWords.map((word: {en: string, ua: string}, idx: number) => {
    return (
      <tr key={`list-info-${idx}`}>
        <td>{word.en}</td>
        <td>{word.ua}</td>
        <th><Field type="checkbox" value={word.en}/></th>
      </tr>
    )
  });

  const onSubmit = (values: InitialValues) => {
    dispatch(create_test({
      name: values.name,
      words: values.words,
      type: "en-ua",
      options: values.options
    }));
    toggle();
  }

  return (
    <Formik initialValues={initialValues} onSubmit={(values) => onSubmit(values)} >
      <Modal isOpen={modal} toggle={toggle}>
        <Form>
          <ModalBody>
            <div className="modal__table">
              <Table className="bg-dark">
                <tbody>
                  { allWordsList }
                </tbody>
              </Table>
            </div>
          </ModalBody>
          <FormGroup>
            <div className="input__name">
              <input className="form-control" type="text" placeholder="name"/>
            </div>
          </FormGroup>
          <ModalFooter>
            <Button color="success" type="submit">SAVE</Button>
            <Button color="danger" onClick={toggle}>UNDO</Button>
          </ModalFooter>
        </Form>
      </Modal>
    </Formik>
  );
}

export default ModalCreated;

export { 
  ModalInfo 
};
