import React, { useState } from 'react';
import { Table } from 'reactstrap';
import "./Dictionary.scss";

const mockData = [
  {
    dictionary: "one",
    words: [
      {en: "sail", ua: "плавати"},
      {en: "some", ua: "декілька"},
      {en: "same", ua: "те саме"},
      {en: "case", ua: "випадок"},
      {en: "cause", ua: "причина"},
      {en: "weak", ua: "слабкий"},
      {en: "therefore", ua: "тому"},
      {en: "fluent", ua: "впливати"},
      {en: "introduce", ua: "представляти"},
      {en: "carry out", ua: "продовжуй"},
    ]
  },
  {
    dictionary: "two",
    words: [
      {en: "sail", ua: "плавати"},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
    ]
  },
  {
    dictionary: "tree",
    words: [
      {en: "sail", ua: "плавати"},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
      {en: "", ua: ""},
    ]
  },
];

const Dictionary: React.FC = () => {
  const [isList, setIsList] = useState(false);
  const toggleList = () => setIsList(!isList);
  const [activeListItem, setActiveListItem] = useState("asd");

  const toggleActiveListItem = (name: string) => {

    if(name === activeListItem) {
      setActiveListItem("");
    } else {
      setActiveListItem(name);
    }
  }

  const list = mockData.map((item: any) => {

    const innerList = item.words.map((item: any, idx: number) => {
      return (
        <tr>
          <th scope="row">{ idx }</th>
          <td>{ item.en }</td>
          <td>{ item.ua }</td>
          <td>answer</td>
        </tr>
      )
    });

    return (
      <div className="table-dictionary-words-wrapper">
        <Table className="table-dictionary-words">
          <thead>
            <tr>
              <th>#</th>
              <th>EN</th>
              <th>UA</th>
              <th>YOUR</th>
            </tr>
          </thead>
          <tbody>
            { innerList }
          </tbody>
        </Table>
        <button className="btn btn-outline-success btn-100">EDIT THE DICTIONARY</button>
      </div>
    )
  });

  const list2 = mockData.map((item: any,) => {
    return (
      <>
        <tbody onClick={() => toggleActiveListItem(item.dictionary)}>
          <tr>
            <th scope="row">
              {item.dictionary}
            </th>
          </tr>
        </tbody>
       {
         activeListItem === item.dictionary && (
          <div className="active-option">
            <button className="btn btn-outline-success">RANDOM</button>
          </div>
         )
       }
      </>
    )
  });

  return (
    <div className="dictionary-page">
      <div className="col-9">
        <div className="dictionary-words">
          <div className="dictionary-words__party">
            { list }
          </div>
        </div>
      </div>
      <div className="col-3">
        <div className="dictionary-options">
          <div className="dictionary-options__party">
              <input type="text" placeholder="filter" className="btn-100" />
            </div>
            <div className="dictionary-options__party">
              <button className="btn btn-outline-success btn-100">Add new dictionary</button>
            </div>
            <div className="dictionary-options__party">
              <button className="btn btn-outline-success btn-100">DELETE</button>
            </div>
            <div className="dictionary-options__party">
              <button className="btn btn-outline-success btn-100" onClick={() => toggleList()}>List</button>
              {
                isList && (
                  <div className="">
                    <Table hover>
                      {
                        list2
                      }
                    </Table>
                  </div>
                )
              }
            </div>
        </div>
      </div>
    </div>
  );
};

export default Dictionary;
