import "./Setting.scss";
import avatar from "../../../../images/photo.jpeg";
import fileReader from '../../../../specialFunction/file-reader';
import { Field, Formik } from 'formik';
import { Button, FormGroup, Label } from 'reactstrap';
import DeleteSetting from './components/DeleteSetting/DeleteSetting';

const Setting: React.FC = () => {
  const ref = useRef(null);

  const onSubmit = (values: any) => {

  };

  return (
    <Formik initialValues={{}} onSubmit={(values: any) => onSubmit(values)}>
      {({values}) => {
        return (
          <div className="setting-page">
            <div className="user-data">
              <header className="user-data-header">

                <div className="set-photo">
                  <FormGroup>
                    <img src={avatar} className="avatar" id="avatar" />
                    <Label>Change Image</Label>
                    <input type="file" className="btn btn-outline-info" accept="image/*" ref={ref} onChange={(e: any) => fileReader(e.target, ref)}/>
                  </FormGroup>
                </div>
              </header>
              <div className="user-data-body">
                <h2>Add tariff plan</h2>
              </div>

              <DeleteSetting />
            </div>
          </div>
        )
      }}
    </Formik>
  )
}

export default Setting;
